sourcesContent:
const { User } = require("../app/schemas/user.js");
const Token = require("../app/schemas/token");
const sendEmail = require("../app/utils/sendEmail");
const crypto = require("crypto");
const Joi = require("joi");
const express = require("express");
const router = express.Router();

router.post("/", async (req, res) => {
    try {
        const schema = Joi.object({ email: Joi.string().email().required() });
        const { error } = schema.validate(req.body);
        if (error) return res.status(400).send(error.details[0].message);

        const user = await User.findOne({ email: req.body.email });
        if (!user)
            return res.status(400).send("user with given email doesn't exist");

        let token = await Token.findOne({ userId: user._id });
        if (!token) {
            token = await new Token({
                userId: user._id,
                token: crypto.randomBytes(32).toString("hex"),
            }).save();
        }

        const link = `${process.env.BASE_URL}/password-reset/${user._id}/${token.token}`;
        await sendEmail(user.email, "Password reset", link);

        alert("O link de alteração de senhar foi enviado para o seu email!");
        res.send("O link de alteração de senha foi enviado para o seu email!");
    } catch (error) {
        res.send("An error occured");
        console.log(error);
    }
});

module.exports = router;

