'use strict'
import mongoose from '../../database/index.js';

const userSchema = mongoose.Schema({
    name: {
        type: String,
        required: true,
        minlength: 3,
        maxlength: 50
    },
    email: {
        type: String,
        required: true,
        minlength: 5,
        maxlength: 255,
        unique: true
    },
    password: {
        type: String,
        required: true,
        minlength: 5,
        maxlength: 255
    },
    createdAt: {
        type: Date,
        default: Date.now
    },
    role: {
        type: String,
        default: 'user',

    }
});

userSchema.plugin(require('mongoose-role'), {
    roles: ['user', 'admin'],
    accessLevels: {
        user: ['user', 'admin'],
        admin: ['admin']
    }
})

const user = mongoose.model('user', userSchema);
export default user;

var newUser = new user({ email: 'email@email.com', role: 'user' })

console.log(newUser.hasAccess('user'))
console.log(newUser.hasAccess('admin'))

